mapper.select:
select problem_id,
       title,
       max(pass_rate) as pass_rate,
       max(score)     as score
from solution
where state + 0 = 1
  and contest_id = #{contestId}
  and uid = #{uid}
group by problem_id, title
order by problem_id;

mapper.update:
update leaderboard
set deleted = true
where uid = #{uid}
