mapper.select:
select sql_calc_found_rows *,
                           state - 1  as state_code,
                           result - 1 as result_code
from judge_result
where user_id = #{userId}
order by submit_time desc
limit #{start}, #{limit};
select found_rows();

select sql_calc_found_rows *,
                           state - 1  as state_code,
                           result - 1 as result_code
from judge_result
where user_id = #{userId}
  and problem_id = #{problemId}
order by submit_time desc
limit #{start}, #{limit};
select found_rows();

select sql_calc_found_rows *,
                           state - 1  as state_code,
                           result - 1 as result_code
from judge_result
where user_id = #{userId}
  and title like concat('%', #{title}, '%')
order by submit_time desc
limit #{start}, #{limit};
select found_rows();
